def disableNodesInList():

    for i in node_list:

        if nuke.toNode(i).knob('disable'):

            nuke.toNode(i).knob('disable').setValue(nuke.thisNode().knobe('disable').value())

        else:
            print " - "+i+" does not have a 'disable' knob. Ignoring..."



